 import React from "react";
function NewAccount(){
    const {
        setLoggedIn,
        setUserName,
        userName,
        setPassword,
        password,
        setToken,
        token
      } = props;

      const newUser = (e) => {
        e.preventDefault();
        try {
          registerUser(userName, password, setToken, setLoggedIn);

        } catch (error) {
          console.error(error);
        }
      };
      console.log(userName);
      console.log(password);

      return (
       <div className="login-box">
        <h1 className="page-title">New Account</h1>
        <form action="" className="form" onSubmit={e=> newUser(e)}>
            <input
            type="text"
            onChange={function(event) {
                setUserName(event.target.value);
                window.localStorage.setItem('username', event.target.value);
            }}
            name="username"
            id="usernameInput"
            required
            />
            <input
            type="text"
            onChange={function(event) {
                setPassword(event.target.value);
                window.localStorage.setItem('password', event.target.value);
            }}
            name="password"
            id="passwordInput"
            required
            />
            <button>Sign Up</button>
        </form>
    </div>
  );
 };
 export default NewAccount;
