import React, { useEffect } from "react";
import { registerNewUser } from ".api.js";

const SignUp = (props) => {
  const {
    userName,
    password,
    setUserName,
    setPassword,
    token,
    setToken,
  } = props;

  const fetchRegUser = (e) => {
    e.preventDefault();
    try {
      registerNewUser(userName, password, setToken, token);

    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="log-in-container">
      <h1 className="page-title">sign up</h1>
      <form action="" className="form" onSubmit={e=> fetchRegUser(e)}>
        <input
          type="text"
          placeholder="Username"
          onChange={function(event) {
            setUserName(event.target.value)
            window.localStorage.setItem('username', event.target.value);
          }}
          name="username"
          id="usernameInput"
          required
        />
        <input
          type="text"
          placeholder="Password"
          onChange={function(event) {
            setPassword(event.target.value);
          }}
          name="password"
          id="passwordInput"
          required
        />
        <button>Register</button>
      </form>
    </div>
  );
};

export default SignUp;
